import React, { useState } from "react";
import { Button, Dialog, DialogTitle, DialogContent, DialogActions } from "@material-ui/core";

export default function Delete({ selectedRows }) {
  const [open, setOpen] = useState(false);

  const handleDelete = () => {
    // Perform the delete operation here using selectedRows
    // You can make an API call to the server or update the state accordingly

    // After successful deletion, close the dialog
    setOpen(false);
  };

  const handleOpen = () => {
    if (selectedRows.length > 0) {
      setOpen(true);
    }
  };

  const handleClose = () => {
    setOpen(false);
  };

  return (
    <div>
      <Button
        variant="contained"
        color="secondary"
        disabled={selectedRows.length === 0}
        onClick={handleOpen}
      >
        Delete
      </Button>

      <Dialog open={open} onClose={handleClose}>
        <DialogTitle>Delete Confirmation</DialogTitle>
        <DialogContent>
          Are you sure you want to delete the selected row(s)?
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose} color="primary">
            Cancel
          </Button>
          <Button onClick={handleDelete} color="secondary">
            Delete
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
}
