import React, { useState } from "react";
import { TextField, Button } from "@material-ui/core";
import axios from "axios";

const AddData = () => {
  const [id, setId] = useState("");
  const [invoice, setInvoice] = useState("");

  const handleAddData = () => {
    // Make a POST request to the AddServlet
    axios
      .post("http://localhost:8080/h2h_project/AddServlet", { id, invoice })
      .then((res) => {
        // Handle the response as needed
        console.log("Add request successful:", res.data);
        // Clear the input fields
        setId("");
        setInvoice("");
      })
      .catch((error) => {
        // Handle the error as needed
        console.error("Add request failed:", error);
      });
  };

  const handleClearData = () => {
    // Clear the input fields
    setId("");
    setInvoice("");
  };

  return (
    <div>
      <TextField
        label="ID"
        value={id}
        onChange={(event) => setId(event.target.value)}
      />
      <TextField
        label="Invoice"
        value={invoice}
        onChange={(event) => setInvoice(event.target.value)}
      />
      <div>
      <Button variant="contained" color="primary" onClick={handleAddData}>
        Add Data
      </Button>
      <Button variant="contained" color="secondary" onClick={handleClearData}>
        Clear Data
      </Button>
      </div>
    </div>
  );
};

export default AddData;
