import React from "react";
import axios from "axios";
import { Button } from "@material-ui/core";

const Delete = ({ selectedRows }) => {
  const handleDelete = () => {
    const idToDelete = selectedRows[0];

    axios
      .delete(`http://localhost:8080/h2h_project/DeleteServlet?id=${idToDelete}`)
      .then(res => {
        // Handle the response as needed
        console.log("Delete request successful:", res.data);
      })
      .catch(error => {
        // Handle the error as needed
        console.error("Delete request failed:", error);
      });
  };

  return (
    <Button
      variant="contained"
      color="secondary"
      disabled={selectedRows.length !== 1} // Disable when no row selected or multiple rows selected
      onClick={handleDelete}
    >
      DELETE
    </Button>
  );
};

export default Delete;
