import React, { useState } from "react";
import axios from "axios";

const Edit = () => {
  const [editedInvoice, setEditedInvoice] = useState(invoice);
  const [selectedInvoice, setSelectedInvoice] = useState(null); //to keep track of selected invoice for editing

  const getEdit = (selectedRow) => {
    axios.get(`http://localhost:8080/h2h_project/EditServlet?id=${selectedRow}`)
      .then(res => {
        // Handle the response from the EditServlet
        console.log("Edit response:", res.data);
      })
      .catch(error => {
        // Handle the error
        console.error("Edit error:", error);
      });
  };
  const handleEditInvoice = (invoice) => {
    setSelectedInvoice(invoice);
  };
  const handleSaveInvoice = (editedInvoice) => {
    const updatedInvoice = invoice.map(item =>
      item.id === selectedInvoice ? { ...item, invoice: editedInvoice } : item
    );
    setInvoice(updatedInvoice);
    setSelectedInvoice(null);
  };
  const handleCancelEdit = () => {
    setSelectedInvoice(null);
  };
  const handleChange = (event) => {
    setEditedInvoice(event.target.value);
  };

  const handleSave = () => {
    onSave(editedInvoice);
  };

  return (
    <div>
      <button
        disabled={selectedRows.length !== 1} //enable if exactly one row is selected
        onClick={() => handleEditInvoice(invoice.id)}
      >EDIT</button>
      <div className="popup">
        <div className="popup-content">
          <h2>Edit</h2>
          <input type="text" value={editedInvoice} onChange={handleChange} />
          <div className="button-container">
            <button onClick={handleSave}>Edit</button>
            <button onClick={onCancel}>Cancel</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default EditPopup;
