import React, { useState } from "react";
import axios from "axios";
import { Button, Dialog, DialogTitle, DialogContent, DialogActions } from "@material-ui/core";
import { withStyles, makeStyles } from '@material-ui/core/styles';
import { deepOrange } from '@material-ui/core/colors';

const ColorButton = withStyles((theme) => ({
  root: {
    color: theme.palette.getContrastText(deepOrange[500]),
    backgroundColor: deepOrange[500],
    '&:hover': {
      backgroundColor: deepOrange[700],
    },
  },
}))(Button);

const useStyles = makeStyles((theme) => ({
  margin: {
    margin: theme.spacing(1),
  },
}));

const Delete = ({ selectedRows }) => {
  const [open, setOpen] = useState(false);
  const [selectedRowData] = useState({ id: "", invoice: "" }); // state for storing selected row
  const classes = useStyles();

  const handleDelete = () => {
    const { id } = selectedRowData;
    const params = new URLSearchParams();
    params.append('id', id);
  
    axios.post("http://localhost:8080/h2h_project/EditServlet", params)
      .then(res => {
        // Handle the response as needed
        console.log("Delete request successful:", res.data);
        setOpen(false);
      })
      .catch(error => {
        // Handle the error as needed
        console.error("Delete request failed:", error);
      });
    setOpen(false);
  };

  const handleOpen = () => {
    if (selectedRows.length > 0) {
      setOpen(true);
    }
  };

  const handleClose = () => {
    setOpen(false);
  };

  return (
    <div>
      <ColorButton
        variant="contained"
        className={classes.margin}
        disabled={selectedRows.length === 0}
        onClick={handleOpen}
      >
        Delete
      </ColorButton>

      <Dialog open={open} onClose={handleClose}>
        <DialogTitle>Delete Records?</DialogTitle>
        <DialogContent>
          Are you sure you want to delete these record[s]?
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose} color="primary">
            Cancel
          </Button>
          <Button onClick={handleDelete} color="secondary">
            Delete
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
}

export default Delete;
