import React, { useEffect, useState } from "react";
import axios from "axios";
import {
  TableContainer,
  Table,
  TableHead,
  TableBody,
  TableRow,
  TableCell,
  TablePagination,
  Checkbox
} from "@material-ui/core";
import { makeStyles } from '@material-ui/core/styles';
import './DataGrid.css';
import RefreshButton from "./RefreshButton";
import Edit from './Edit';
import Delete from "./Delete";

const rowsPerPageOptions = [5, 10, 20, 50, 100];
const useStyles = makeStyles((theme) => ({
  margin: {
    margin: theme.spacing(1),
  },
  buttonContainer: {
    display: 'flex',
    justifyContent: 'flex-start',
  },
}));

export default function Grid() {
  const [invoice, setInvoice] = useState([]);
  const [selectAll, setSelectAll] = useState(false);
  const [selectedRows, setSelectedRows] = useState([]); //to keep track of selected rows
  //pagination
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(rowsPerPageOptions[0]);

  useEffect(() => {
    getData();
  }, []);

  const getData = () => {
    axios.get("http://localhost:8080/h2h_project/DataLoadingServlet")
      .then(res => {
        const fetchedInvoice = res.data;
        setInvoice(fetchedInvoice);
      })
      .catch(error => {
        // Handle the error as needed
        console.error("Failed to fetch data:", error);
      });
  };

  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };
  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setPage(0);
  };

  //CHECKBOX
  const handleCheckboxChange = (event, id) => {
    const isChecked = event.target.checked;
    if (isChecked) {
      // Add the selected row to the array
      setSelectedRows([...selectedRows, id]);
    } else {
      // Remove the deselected row from the array
      setSelectedRows(selectedRows.filter(rowId => rowId !== id));
    }

    const updatedInvoice = invoice.map(item =>
      item.id === id ? { ...item, checked: event.target.checked } : item
    );
    setInvoice(updatedInvoice);
  };

  //MASTERCHECKBOX
  const handleMasterCheckboxChange = (event) => {
    const checked = event.target.checked;
    const updatedInvoice = invoice.map(item => ({
      ...item,
      checked
    }));
    setInvoice(updatedInvoice);
    setSelectAll(checked);

    if (!checked) { //clear the selected rows when the master checkbox is unchecked
      setSelectedRows([]);
    }
  };

  const classes = useStyles();

  return (
    <div className="DataGrid">
      <TableContainer>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>
                <Checkbox
                  checked={selectAll}
                  onChange={handleMasterCheckboxChange}
                />
              </TableCell>
              <TableCell>ID</TableCell>
              <TableCell>Invoice</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {invoice.map(Invoice => (
              <TableRow key={Invoice.id}>
                <TableCell>
                  <Checkbox
                    checked={Invoice.checked || false}
                    onChange={event => handleCheckboxChange(event, Invoice.id)}
                  />
                </TableCell>
                <TableCell>{Invoice.id}</TableCell>
                <TableCell>{Invoice.invoice}</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
      <TablePagination
        component="div"
        count={invoice.length}
        page={page}
        rowsPerPage={rowsPerPage}
        rowsPerPageOptions={rowsPerPageOptions}
        onChangePage={handleChangePage}
        onChangeRowsPerPage={handleChangeRowsPerPage}
      />
      <div className={classes.buttonContainer}>
      <RefreshButton onClick={getData}/>
      <Edit invoice={invoice} setInvoice={setInvoice} selectedRows={selectedRows} />
      <Delete id={invoice.id} selectedRows={selectedRows} />
      </div>
    </div>
  );
}
