import React from "react";
import { Tabs as MuiTabs, Tab, Box, InputBase } from "@material-ui/core";
import Button from '@material-ui/core/Button';
import Grid from './Grid';
import AddData from "./AddData";

const ColorButton = withStyles((theme) => ({
  root: {
    color: "#8fd163",
    backgroundColor: "#8fd163",
    '&:hover': {
      backgroundColor: "#8fd163",
    },
  },
}))(Button);

const TabContent = () => {
  const [selectedTab, setSelectedTab] = React.useState(0);

  const handleTabChange = (event, newValue) => {
    setSelectedTab(newValue);
  };

  return (
      <Box>
        <Box display="flex" justifyContent="flex-end" alignItems="flex-end">
          <InputBase
            placeholder="Search Customer Order ID"
            style={{ backgroundColor: "white", margin: "10px 5px 5px 10px" }}
          />
          <ColorButton variant="contained">
            Advanced Search
          </ColorButton>
          </Box>
        <MuiTabs value={selectedTab} onChange={handleTabChange}>
          <Tab label="Homepage" />
          <Tab label="Add Data" />
          <Tab label="Analytical View" />
        </MuiTabs>
        <Box p={3}>
          {selectedTab === 0 && <HomepageTab />}
          {selectedTab === 1 && <AddDataTab />}
          {selectedTab === 2 && <AnalyticalViewTab />}
        </Box>
      </Box>
  );
};

const HomepageTab = () => {
  // Implement the content for the Homepage tab (table and buttons) here
  return <div><Grid /></div>;
};

const AddDataTab = () => {
  // Implement the content for the Add Data tab here
  return <div><AddData /></div>;
};

const AnalyticalViewTab = () => {
  // Implement the content for the Analytical View tab here
  return <div>Analytical View Content</div>;
};

export default TabContent;
