import React from "react";

const EditButton = ({ disabled, onClick }) => {
    const handleEdit = (selectedRow) => {
        // Handle the edit operation for the selected row
        console.log("Edit row:", selectedRow);
    };

    return (
        <button disabled={selectedRows.length !== 1} //enable if exactly one row is selected
            onClick={() => handleEdit(selectedRows[0])}>
            EDIT
        </button>
    );
};

export default EditButton;
