import React, { useState } from "react";
import axios from "axios";
import {
  Button,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField
} from "@material-ui/core";

const ColorButton = withStyles((theme) => ({
  root: {
    color: theme.palette.getContrastText(deepOrange[500]),
    backgroundColor: deepOrange[500],
    '&:hover': {
      backgroundColor: deepOrange[700],
    },
  },
}))(Button);

const useStyles = makeStyles((theme) => ({
  margin: {
    margin: theme.spacing(1),
  },
  buttonContainer: {
    display: 'flex',
    justifyContent: 'flex-start',
  },
}));

const Edit = ( {invoice, setInvoice, selectedRows} ) => {
  const [editDialogOpen, setEditDialogOpen] = useState(false); // state for edit dialog
  const [selectedRowData, setSelectedRowData] = useState({ id: "", invoice: "" }); // state for storing selected row data
  const classes = useStyles();
  // EDIT BUTTON
  const handleEditClick = () => {
    const selectedRow = invoice.find(item => item.id === selectedRows[0]);
    setSelectedRowData(selectedRow);
    setEditDialogOpen(true);
  };

  const handleEditDialogClose = () => {
    setEditDialogOpen(false);
  };

  const handleEditDialogSave = () => {
    // Perform the save operation using Axios and the 'EditServlet'
    const { id, invoice } = selectedRowData;
    axios.post("http://localhost:8080/h2h_project/EditServlet", { id, invoice })
      .then(res => {
        // Handle the response as needed
        console.log("Edit request successful:", res.data);
        setEditDialogOpen(false);
      })
      .catch(error => {
        // Handle the error as needed
        console.error("Edit request failed:", error);
      });
  };

  return (
    <div>
      <ColorButton
        variant="contained"
        color="primary"
        className={classes.margin}
        disabled={selectedRows.length !== 1} // Disable when no row selected or multiple rows selected
        onClick={handleEditClick}
      >
        EDIT
      </ColorButton>
      <Dialog open={editDialogOpen} onClose={handleEditDialogClose}>
        <DialogTitle>Edit Row</DialogTitle>
        <DialogContent>
          <TextField
            label="ID"
            value={selectedRowData.id}
            disabled
          />
          <TextField
            label="Invoice"
            value={selectedRowData.invoice}
            onChange={event => setSelectedRowData(prevState => ({ ...prevState, invoice: event.target.value }))}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleEditDialogClose} color="secondary">
            Cancel
          </Button>
          <Button onClick={handleEditDialogSave} color="primary">
            Save
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
};

export default Edit;
